import express from "express";
import cors from "cors";
import fetch from "node-fetch";

const app = express();
app.use(cors());
app.use(express.json());
app.use(express.static("public")); // sirve el index.html

// Endpoint para conectar con Ollama (respuesta completa, sin streaming)
app.post("/chat", async (req, res) => {
  try {
    const { prompt } = req.body;

    const response = await fetch("http://localhost:11434/api/generate", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({
        model: "gemma3:4b",
        prompt: prompt,
        stream: false // 👈 respuesta en bloque
      }),
    });

    const data = await response.json();
    console.log("Respuesta de Ollama:", data); // 👀 para debug

    res.json({
      reply: data.response || data.message || "⚠️ No hubo respuesta del modelo."
    });
  } catch (err) {
    console.error("Error proxy a Ollama:", err);
    res.status(500).json({ error: "Error al conectar con Ollama" });
  }
});

app.listen(3000, () => {
  console.log("✅ Servidor corriendo en http://localhost:3000");
});
